import React, { useState } from "react";
import { Button } from "@/components/ui/button";

export default function Calculator() {
  const [input, setInput] = useState("");
  const [result, setResult] = useState("");

  const handleClick = (value) => {
    if (value === "C") {
      setInput("");
      setResult("");
    } else if (value === "=") {
      try {
        setResult(eval(input).toString());
      } catch {
        setResult("Error");
      }
    } else {
      setInput((prev) => prev + value);
    }
  };

  return (
    <div className="max-w-sm mx-auto p-4 bg-gray-900 text-white rounded-lg shadow-lg text-center">
      <div className="text-xl bg-gray-800 p-2 rounded mb-2 min-h-[50px]">{input || "0"}</div>
      <div className="text-2xl bg-gray-700 p-2 rounded mb-4 min-h-[50px]">{result}</div>
      <div className="grid grid-cols-4 gap-2">
        {["7", "8", "9", "/", "4", "5", "6", "*", "1", "2", "3", "-", "0", ".", "=", "+"].map((item) => (
          <Button key={item} onClick={() => handleClick(item)} className="p-4 bg-gray-600 hover:bg-gray-500">
            {item}
          </Button>
        ))}
        <Button onClick={() => handleClick("C")} className="col-span-4 bg-red-600 hover:bg-red-500 p-4">
          C
        </Button>
      </div>
    </div>
  );
}
